void GetSysInfo(_ESYSINFO* pSysInfo) {
	pSysInfo->winsys = W2Estr(Getwin());
	pSysInfo->user = W2Estr(GetUsername());
	pSysInfo->isx64 = SysIsx64();
	OSVERSIONINFOEXW osvi;
	osvi.dwOSVersionInfoSize = sizeof(OSVERSIONINFOEXW);
	BOOL bOsVersionInfoEx;
	if (!(bOsVersionInfoEx = RtlGetVersion((OSVERSIONINFOW*)&osvi)))
	{
		osvi.dwOSVersionInfoSize = sizeof(OSVERSIONINFOW);
		RtlGetVersion((OSVERSIONINFOW*)&osvi);
	}
	pSysInfo->vsion = W2Estr(to_wstring(osvi.dwMajorVersion + osvi.dwMinorVersion * 0.1));
	pSysInfo->lastpack = W2Estr(to_wstring(osvi.wServicePackMajor + osvi.wServicePackMinor * 0.1));
	pSysInfo->buildvsion = osvi.dwBuildNumber;;
	wchar_t pszPath[MAX_PATH];
	GetWindowsDirectoryW(pszPath, MAX_PATH);
	pSysInfo->windir = W2Estr(GetWinDir());
	pSysInfo->sysdir = W2Estr(GetWinSysDir());
	pSysInfo->code = GetOEMCP();
	pSysInfo->locallanguage = W2Estr(GetSyslangugage());
	pSysInfo->iso = W2Estr(Getcountry());
	pSysInfo->money = W2Estr(GetLocalmaney());
	pSysInfo->countyrcode = GetPhoneCode();
	pSysInfo->countryname = W2Estr(GetCountyName());
	TIME_ZONE_INFORMATION m_timezone;
	GetTimeZoneInformation(&m_timezone);
	pSysInfo->timezone = W2Estr(m_timezone.StandardName);
	pSysInfo->timeformat = W2Estr(GetSYSDATA());
	pSysInfo->dataformat = W2Estr(Gettimedata());
}
